@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(220, 14%, 96%);
  --foreground: hsl(222, 84%, 5%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 84%, 5%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 84%, 5%);
  --primary: hsl(221, 83%, 53%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(210, 40%, 94%);
  --secondary-foreground: hsl(222, 47%, 11%);
  --muted: hsl(210, 40%, 94%);
  --muted-foreground: hsl(215, 16%, 47%);
  --accent: hsl(210, 40%, 94%);
  --accent-foreground: hsl(222, 47%, 11%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --ring: hsl(221, 83%, 53%);
  --chart-1: hsl(221, 83%, 53%);
  --chart-2: hsl(159, 100%, 36%);
  --chart-3: hsl(42, 93%, 56%);
  --chart-4: hsl(147, 79%, 42%);
  --chart-5: hsl(341, 75%, 51%);
  --radius: 0.75rem;
  --font-sans: 'Inter', system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
}

.dark {
  --background: hsl(222, 84%, 5%);
  --foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 47%, 11%);
  --card-foreground: hsl(210, 40%, 98%);
  --popover: hsl(222, 47%, 11%);
  --popover-foreground: hsl(210, 40%, 98%);
  --primary: hsl(221, 83%, 53%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(217, 32%, 17%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --ring: hsl(221, 83%, 53%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    scroll-behavior: smooth;
  }

  @media (prefers-reduced-motion: reduce) {
    html, body {
      scroll-behavior: auto;
    }
  }
}

@layer components {
  .gradient-bg {
    background: linear-gradient(135deg, hsl(221, 83%, 53%) 0%, hsl(230, 81%, 60%) 50%, hsl(208, 100%, 46%) 100%);
    position: relative;
    overflow: hidden;
  }

  .gradient-bg::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 80%, rgba(255,255,255,0.1) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(255,255,255,0.15) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(255,255,255,0.08) 0%, transparent 70%);
    animation: backgroundFloat 20s ease-in-out infinite;
  }

  @keyframes backgroundFloat {
    0%, 100% { transform: translateX(0px) translateY(0px) rotate(0deg); }
    33% { transform: translateX(-30px) translateY(-50px) rotate(1deg); }
    66% { transform: translateX(20px) translateY(-20px) rotate(-1deg); }
  }

  .skill-bar {
    transition: width 2s ease-in-out;
    position: relative;
    overflow: hidden;
  }

  .skill-bar::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    width: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
    animation: shimmer 2s infinite;
    transform: translateX(-100%);
  }

  @keyframes shimmer {
    100% { transform: translateX(100%); }
  }

  .card-hover {
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    position: relative;
    overflow: hidden;
  }

  .card-hover::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
    transition: left 0.5s;
  }

  .card-hover:hover::before {
    left: 100%;
  }

  .card-hover:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 
      0 25px 50px -12px rgba(0, 0, 0, 0.15),
      0 0 0 1px rgba(255, 255, 255, 0.05),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .fade-in {
    animation: fadeInUp 1s ease-out;
  }

  @keyframes fadeInUp {
    from { 
      opacity: 0; 
      transform: translateY(40px);
    }
    to { 
      opacity: 1; 
      transform: translateY(0);
    }
  }

  .floating-resume {
    position: fixed;
    bottom: 2rem;
    right: 2rem;
    z-index: 50;
    animation: floatBounce 3s ease-in-out infinite;
  }

  @keyframes floatBounce {
    0%, 100% { transform: translateY(0px) scale(1); }
    25% { transform: translateY(-5px) scale(1.05); }
    50% { transform: translateY(-10px) scale(1.1); }
    75% { transform: translateY(-5px) scale(1.05); }
  }

  .section-bg-pattern {
    position: relative;
  }

  .section-bg-pattern::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      radial-gradient(circle at 1px 1px, rgba(255,255,255,0.15) 1px, transparent 0);
    background-size: 20px 20px;
    opacity: 0.3;
    animation: patternMove 30s linear infinite;
  }

  @keyframes patternMove {
    0% { transform: translate(0, 0); }
    100% { transform: translate(20px, 20px); }
  }

  /* Aurora Hero Background */
  .hero-aurora {
    position: relative;
    overflow: hidden;
    background: 
      conic-gradient(from 230.29deg at 51.63% 52.16%, 
        rgb(36, 0, 255) 0deg, 
        rgb(0, 135, 255) 67.5deg, 
        rgb(108, 39, 157) 198.75deg, 
        rgb(24, 38, 163) 251.25deg, 
        rgb(54, 103, 196) 301.88deg, 
        rgb(105, 30, 255) 360deg),
      radial-gradient(ellipse 80% 50% at 50% -20%,
        rgba(120, 119, 198, 0.3), 
        transparent 50%),
      linear-gradient(180deg, 
        rgba(0, 17, 82, 0.8) 0%, 
        rgba(24, 38, 163, 0.6) 100%);
    animation: aurora-shift 15s ease-in-out infinite;
  }

  .hero-aurora::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      radial-gradient(circle at 25% 25%, rgba(255, 255, 255, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, rgba(120, 119, 198, 0.1) 0%, transparent 50%),
      linear-gradient(0deg, transparent 24%, rgba(255, 255, 255, 0.05) 25%, rgba(255, 255, 255, 0.05) 26%, transparent 27%, transparent 74%, rgba(255, 255, 255, 0.05) 75%, rgba(255, 255, 255, 0.05) 76%, transparent 77%, transparent);
    background-size: 75px 75px;
    animation: grid-move 20s linear infinite;
    opacity: 0.6;
    pointer-events: none;
  }

  .hero-aurora::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      repeating-linear-gradient(
        0deg,
        transparent,
        transparent 2px,
        rgba(255, 255, 255, 0.03) 2px,
        rgba(255, 255, 255, 0.03) 4px
      );
    animation: grain-move 8s linear infinite;
    opacity: 0.4;
    pointer-events: none;
  }

  @keyframes aurora-shift {
    0%, 100% { 
      filter: hue-rotate(0deg) brightness(1); 
    }
    33% { 
      filter: hue-rotate(30deg) brightness(1.1); 
    }
    66% { 
      filter: hue-rotate(-20deg) brightness(0.9); 
    }
  }

  @keyframes grid-move {
    0% { transform: translate(0, 0) rotate(0deg); }
    100% { transform: translate(75px, 75px) rotate(360deg); }
  }

  @keyframes grain-move {
    0%, 100% { transform: translate(0, 0); }
    25% { transform: translate(-5px, -5px); }
    50% { transform: translate(5px, -5px); }
    75% { transform: translate(-5px, 5px); }
  }

  /* Reduce motion for accessibility */
  @media (prefers-reduced-motion: reduce) {
    .hero-aurora,
    .hero-aurora::before,
    .hero-aurora::after {
      animation: none;
    }
  }

  /* Glowing Avatar Ring */
  .avatar-glow {
    position: relative;
    border-radius: 50%;
    overflow: hidden;
    transition: transform 0.3s ease-out;
  }

  .avatar-glow::before {
    content: '';
    position: absolute;
    top: -4px;
    left: -4px;
    right: -4px;
    bottom: -4px;
    background: conic-gradient(
      from 0deg,
      #ff006e,
      #8338ec,
      #3a86ff,
      #06ffa5,
      #ffbe0b,
      #ff006e
    );
    border-radius: 50%;
    animation: ring-rotate 3s linear infinite;
    z-index: -1;
  }

  .avatar-glow::after {
    content: '';
    position: absolute;
    top: -8px;
    left: -8px;
    right: -8px;
    bottom: -8px;
    background: radial-gradient(
      circle,
      rgba(255, 255, 255, 0.1) 0%,
      transparent 70%
    );
    border-radius: 50%;
    animation: glow-pulse 2s ease-in-out infinite alternate;
    z-index: -2;
  }

  @keyframes ring-rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  @keyframes glow-pulse {
    from { 
      opacity: 0.5;
      transform: scale(1);
    }
    to { 
      opacity: 0.8;
      transform: scale(1.05);
    }
  }

  .parallax-container {
    transition: transform 0.1s ease-out;
  }

  /* Reduce motion for accessibility */
  @media (prefers-reduced-motion: reduce) {
    .avatar-glow::before,
    .avatar-glow::after {
      animation: none;
    }
    .parallax-container {
      transition: none;
    }
  }

  /* Role Rotator Animation */
  .role-rotator {
    display: inline-block;
    position: relative;
    overflow: hidden;
    min-width: 280px;
    text-align: center;
  }

  .role-text {
    display: inline-block;
    opacity: 0;
    transform: translateY(20px);
    animation: roleSlideIn 2.5s ease-in-out infinite;
    position: absolute;
    width: 100%;
    left: 0;
  }

  .role-text.active {
    opacity: 1;
    transform: translateY(0);
    position: relative;
  }

  @keyframes roleSlideIn {
    0%, 20% {
      opacity: 0;
      transform: translateY(20px);
    }
    30%, 70% {
      opacity: 1;
      transform: translateY(0);
    }
    80%, 100% {
